import{_ as a,c as d,o as r,ag as e}from"./chunks/framework.D4Vqf8I7.js";const p=JSON.parse('{"title":"Архитектура аппаратных средств","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"docs/disciplines/aas/index.md","filePath":"docs/disciplines/aas/index.md","lastUpdated":1759831451000}'),l={name:"docs/disciplines/aas/index.md"};function o(i,t,h,n,s,c){return r(),d("div",null,t[0]||(t[0]=[e('<h1 id="архитектура-аппаратных-средств" tabindex="-1">Архитектура аппаратных средств <a class="header-anchor" href="#архитектура-аппаратных-средств" aria-label="Permalink to &quot;Архитектура аппаратных средств&quot;">​</a></h1><h2 id="учебное-пособие" tabindex="-1">Учебное пособие <a class="header-anchor" href="#учебное-пособие" aria-label="Permalink to &quot;Учебное пособие&quot;">​</a></h2><p>Изучение теории и всех основных колмпьютерных технологий, чтобы они хотя бы понимали принцип работы компьютера. Используем <a href="/wiki/docs/wi/logism.html">Logisim Evolution</a> для моделирования процессора и прочего логического</p><p><a href="http://freeschool.altlinux.ru/?p=5138" target="_blank" rel="noreferrer">http://freeschool.altlinux.ru/?p=5138</a><br><a href="https://nizrp.narod.ru/metod/kafinfizmtex/1678067360.pdf" target="_blank" rel="noreferrer">https://nizrp.narod.ru/metod/kafinfizmtex/1678067360.pdf</a><br><a href="https://www.google.com/url?sa=t&amp;source=web&amp;rct=j&amp;opi=89978449&amp;url=http://st4.reshaem.net/tasks/task_48207.pdf&amp;ved=2ahUKEwjY-t6DoeSOAxVdEBAIHWiuIU4QFnoECBkQAQ&amp;usg=AOvVaw3EwH5_LlX1fBRqef6LP77s" target="_blank" rel="noreferrer">МГУП Попов Д.И., Лилов И.П. Организация ЭВМ Лабораторные работы в программе Logisim</a> - . ВСЕ ЛАБЫ ВЗЯТЫ ОТСЮДА, ничего особо придумывать не нужно</p><p>В качестве базового учебника мне нравится старая книжка: <a href="https://kfilial.mggeu.ru/wp-content/uploads/2021/02/Maksimov-N.V.-Arhitektura-EVM-i-vychisl.-sistem.pdf" target="_blank" rel="noreferrer">Н. В. Максимов, Т. Л. Партыка, И. И. Попов АРХИТЕКТУРА ЭВМ И ВЫЧИСЛИТЕЛЬНЫХ СИСТЕМ</a>. Она достаточно проста, при этом в неё много материала (более 500 страниц). Да, там куча устаревшего материала, но тем ни менее, база она и в африке база.</p><p>Самостаятельные работы являются продолжение практических, но дается другое задание (В исходных лабораторках идут несколько заданий в каждой из)</p><h2 id="тематическии-план" tabindex="-1">Тематический план <a class="header-anchor" href="#тематическии-план" aria-label="Permalink to &quot;Тематический план&quot;">​</a></h2><table id="program-table"><thead><tr><th>Наименование разделов и тем</th><th>Содержание учебного материала и формы организации деятельности обучающихся</th><th>Уровень освоения</th><th>Объем часов</th><th>Осваиваемые элементы компетенций</th></tr><tr><th>1</th><th>2</th><th>3</th><th>4</th><th>5</th></tr></thead><tbody><tr><td colspan="5">Раздел 1. Вычислительные приборы и устройства</td></tr><tr><td rowspan="2">Тема 1.1 Основные сведения о вычислительных системах</td><td colspan="3">Содержание учебного материала</td><td rowspan="2">ОК ПК</td></tr><tr><td>История вычислительных приборов и устройств. Классы вычислительных машин.</td><td>1</td><td>2</td></tr><tr><td rowspan="21">Тема 1.2 Логические основы ЭВМ, элементы и узлы.</td><td colspan="3">Содержание учебного материала</td><td rowspan="21">ОК ПК</td></tr><tr><td>Логические основы ЭВМ, элементы и узлы. Синхронные и асинхронные устройства.</td><td>1</td><td>2</td></tr><tr><td>Основы работы с цифровыми устройствами в программе Logism</td><td>1</td><td>2</td></tr><tr><td>Комбинационные и последовательные логические устройства</td><td>1</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Знакомство с программой Logisim. Комбинационные устройства. Минимизация</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Знакомство с программой Logisim. Комбинационные устройства. Минимизация</td><td>3</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Элементарные устройства памяти</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Элементарные устройства памяти</td><td>3</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Декодер, мультиплексор и демультиплексор</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Декодер, мультиплексор и демультиплексор</td><td>3</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Декодер, мультиплексор и демультиплексор</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Декодер, мультиплексор и демультиплексор</td><td>3</td><td>2</td></tr><tr><td colspan="3">Контрольная работа: Вычислительные приборы и устройства</td></tr><tr><td colspan="5">Раздел 2. Архитектура и структура вычислительных систем</td></tr><tr><td rowspan="5">Тема 2.1 Базовые представления об архитектуре ЭВМ</td><td colspan="3">Содержание учебного материала</td><td rowspan="5">ОК ПК</td></tr><tr><td>Устройства на жёсткой и программируемой логике</td><td>1</td><td>2</td></tr><tr><td>Базовые компоненты архитектур ЭВМ</td><td>1</td><td>2</td></tr><tr><td>Архитектура Фон-Неймана</td><td>1</td><td>2</td></tr><tr><td>Гарвардская архитектура</td><td>1</td><td>2</td></tr><tr><td rowspan="9">Тема 2.2 Архитектура процессора</td><td colspan="3">Содержание учебного материала</td><td rowspan="9">ОК ПК</td></tr><tr><td>Процессор, структура и функционирование, прерывания</td><td>1</td><td>2</td></tr><tr><td>Структура Арифметически-логического устройства</td><td>1</td><td>2</td></tr><tr><td>Структура блока управления, регистров и кэш-памяти</td><td>1</td><td>2</td></tr><tr><td>Классы процессоров</td><td>1</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Тактовые импульсы. Шина</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Тактовые импульсы. Шина</td><td>3</td><td>2</td></tr><tr><td rowspan="19">Тема 2.3 Архитектура устройств памяти, ввода и вывода</td><td colspan="3">Содержание учебного материала</td><td rowspan="19">ОК ПК</td></tr><tr><td>Организация оперативной памяти</td><td>1</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Сложные устройства памяти: ОЗУ и ПЗУ</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Сложные устройства памяти: ОЗУ и ПЗУ</td><td>3</td><td>2</td></tr><tr><td colspan="3">Содержание учебного материала</td></tr><tr><td>Интерфейсы устройств ввода и вывода</td><td>1</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Устройства ввода и вывода</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Устройства ввода и вывода</td><td>3</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Ассемблер</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Ассемблер</td><td>3</td><td>2</td></tr><tr><td colspan="3">Содержание учебного материала</td></tr><tr><td>Носители информации</td><td>1</td><td>2</td></tr><tr><td colspan="3">Контрольная работа: Архитектура и структура вычислительных систем</td></tr><tr><td colspan="5">Раздел 3. Работа вычислительной системы до загрузки операционной системы</td></tr><tr><td rowspan="4">Тема 3.1 Базовая система ввода и вывода</td><td colspan="3">Содержание учебного материала</td><td rowspan="4">ОК ПК</td></tr><tr><td>Процесс загрузки вычислительной системы</td><td>1</td><td>2</td></tr><tr><td>BIOS и UIFI</td><td>1</td><td>2</td></tr><tr><td colspan="3">Контрольная работа: Работа вычислительной системы до загрузки операционной системы</td></tr></tbody><tfoot><tr><td colspan="3">Консультации</td><td>8</td><td> </td></tr><tr><td colspan="3">Экзамен</td><td>8</td><td> </td></tr><tr><td colspan="3">Всего</td><td>88</td><td> </td></tr></tfoot></table><h2 id="контрольно-оценочные-средства-промежуточнои-аттестации" tabindex="-1">Контрольно-оценочные средства промежуточной аттестации <a class="header-anchor" href="#контрольно-оценочные-средства-промежуточнои-аттестации" aria-label="Permalink to &quot;Контрольно-оценочные средства промежуточной аттестации&quot;">​</a></h2><h3 id="вопросы-для-подготовки" tabindex="-1">Вопросы для подготовки: <a class="header-anchor" href="#вопросы-для-подготовки" aria-label="Permalink to &quot;Вопросы для подготовки:&quot;">​</a></h3><ol><li></li><li></li></ol><h3 id="форма-промежуточнои-аттестации-экзамен-по-билетам" tabindex="-1">Форма промежуточной аттестации: Экзамен (по билетам) <a class="header-anchor" href="#форма-промежуточнои-аттестации-экзамен-по-билетам" aria-label="Permalink to &quot;Форма промежуточной аттестации: Экзамен (по билетам)&quot;">​</a></h3><h4 id="билет-1" tabindex="-1">Билет 1 <a class="header-anchor" href="#билет-1" aria-label="Permalink to &quot;Билет 1&quot;">​</a></h4><h4 id="билет-2" tabindex="-1">Билет 2 <a class="header-anchor" href="#билет-2" aria-label="Permalink to &quot;Билет 2&quot;">​</a></h4><h4 id="билет-3" tabindex="-1">Билет 3 <a class="header-anchor" href="#билет-3" aria-label="Permalink to &quot;Билет 3&quot;">​</a></h4><h4 id="билет-4" tabindex="-1">Билет 4 <a class="header-anchor" href="#билет-4" aria-label="Permalink to &quot;Билет 4&quot;">​</a></h4><h4 id="билет-5" tabindex="-1">Билет 5 <a class="header-anchor" href="#билет-5" aria-label="Permalink to &quot;Билет 5&quot;">​</a></h4><h4 id="билет-6" tabindex="-1">Билет 6 <a class="header-anchor" href="#билет-6" aria-label="Permalink to &quot;Билет 6&quot;">​</a></h4><h4 id="билет-7" tabindex="-1">Билет 7 <a class="header-anchor" href="#билет-7" aria-label="Permalink to &quot;Билет 7&quot;">​</a></h4><h4 id="билет-8" tabindex="-1">Билет 8 <a class="header-anchor" href="#билет-8" aria-label="Permalink to &quot;Билет 8&quot;">​</a></h4><h4 id="билет-9" tabindex="-1">Билет 9 <a class="header-anchor" href="#билет-9" aria-label="Permalink to &quot;Билет 9&quot;">​</a></h4><h4 id="билет-10" tabindex="-1">Билет 10 <a class="header-anchor" href="#билет-10" aria-label="Permalink to &quot;Билет 10&quot;">​</a></h4><h4 id="билет-11" tabindex="-1">Билет 11 <a class="header-anchor" href="#билет-11" aria-label="Permalink to &quot;Билет 11&quot;">​</a></h4><h4 id="билет-12" tabindex="-1">Билет 12 <a class="header-anchor" href="#билет-12" aria-label="Permalink to &quot;Билет 12&quot;">​</a></h4><h4 id="билет-13" tabindex="-1">Билет 13 <a class="header-anchor" href="#билет-13" aria-label="Permalink to &quot;Билет 13&quot;">​</a></h4><h4 id="билет-14" tabindex="-1">Билет 14 <a class="header-anchor" href="#билет-14" aria-label="Permalink to &quot;Билет 14&quot;">​</a></h4><h4 id="билет-15" tabindex="-1">Билет 15 <a class="header-anchor" href="#билет-15" aria-label="Permalink to &quot;Билет 15&quot;">​</a></h4><h4 id="билет-16" tabindex="-1">Билет 16 <a class="header-anchor" href="#билет-16" aria-label="Permalink to &quot;Билет 16&quot;">​</a></h4><h4 id="билет-17" tabindex="-1">Билет 17 <a class="header-anchor" href="#билет-17" aria-label="Permalink to &quot;Билет 17&quot;">​</a></h4><h4 id="билет-18" tabindex="-1">Билет 18 <a class="header-anchor" href="#билет-18" aria-label="Permalink to &quot;Билет 18&quot;">​</a></h4><h4 id="билет-19" tabindex="-1">Билет 19 <a class="header-anchor" href="#билет-19" aria-label="Permalink to &quot;Билет 19&quot;">​</a></h4><h4 id="билет-20" tabindex="-1">Билет 20 <a class="header-anchor" href="#билет-20" aria-label="Permalink to &quot;Билет 20&quot;">​</a></h4><h4 id="билет-21" tabindex="-1">Билет 21 <a class="header-anchor" href="#билет-21" aria-label="Permalink to &quot;Билет 21&quot;">​</a></h4><h4 id="билет-22" tabindex="-1">Билет 22 <a class="header-anchor" href="#билет-22" aria-label="Permalink to &quot;Билет 22&quot;">​</a></h4><h4 id="билет-23" tabindex="-1">Билет 23 <a class="header-anchor" href="#билет-23" aria-label="Permalink to &quot;Билет 23&quot;">​</a></h4><h4 id="билет-24" tabindex="-1">Билет 24 <a class="header-anchor" href="#билет-24" aria-label="Permalink to &quot;Билет 24&quot;">​</a></h4><h4 id="билет-25" tabindex="-1">Билет 25 <a class="header-anchor" href="#билет-25" aria-label="Permalink to &quot;Билет 25&quot;">​</a></h4><h4 id="билет-26" tabindex="-1">Билет 26 <a class="header-anchor" href="#билет-26" aria-label="Permalink to &quot;Билет 26&quot;">​</a></h4><h4 id="билет-27" tabindex="-1">Билет 27 <a class="header-anchor" href="#билет-27" aria-label="Permalink to &quot;Билет 27&quot;">​</a></h4><h4 id="билет-28" tabindex="-1">Билет 28 <a class="header-anchor" href="#билет-28" aria-label="Permalink to &quot;Билет 28&quot;">​</a></h4><h4 id="билет-29" tabindex="-1">Билет 29 <a class="header-anchor" href="#билет-29" aria-label="Permalink to &quot;Билет 29&quot;">​</a></h4><h4 id="билет-30" tabindex="-1">Билет 30 <a class="header-anchor" href="#билет-30" aria-label="Permalink to &quot;Билет 30&quot;">​</a></h4><h4 id="билет-31" tabindex="-1">Билет 31 <a class="header-anchor" href="#билет-31" aria-label="Permalink to &quot;Билет 31&quot;">​</a></h4><h4 id="билет-32" tabindex="-1">Билет 32 <a class="header-anchor" href="#билет-32" aria-label="Permalink to &quot;Билет 32&quot;">​</a></h4><h3 id="критерии-оценки" tabindex="-1">Критерии оценки <a class="header-anchor" href="#критерии-оценки" aria-label="Permalink to &quot;Критерии оценки&quot;">​</a></h3><p><strong>Оценка «отлично»</strong> выставляется обучающемуся, если:</p><ul><li>глубоко и прочно усвоил программный материал курса</li><li>исчерпывающе, последовательно, четко и логически стройно его излагает</li><li>умеет тесно увязывать теорию с практикой</li><li>свободно справляется с задачами и вопросами</li><li>не затрудняется с ответами при видоизменении заданий</li><li>правильно обосновывает принятые решения</li></ul><p><strong>Оценка «хорошо»</strong> выставляется обучающемуся, если:</p><ul><li>твердо знает материал курса</li><li>грамотно и по существу излагает его</li><li>не допускает существенных неточностей в ответе на вопрос</li><li>правильно применяет теоретические положения при решении практических вопросов и задач</li><li>владеет необходимыми навыками и приемами их выполнения</li></ul><p><strong>Оценка «удовлетворительно»</strong> выставляется обучающемуся, если:</p><ul><li>имеет знания только основного материала</li><li>не усвоил деталей материала</li><li>допускает неточности и недостаточно правильные формулировки</li><li>нарушает логическую последовательность в изложении</li><li>испытывает затруднения при выполнении задач</li></ul><p><strong>Оценка «неудовлетворительно»</strong> выставляется обучающемуся, если:</p><ul><li>не знает значительной части программного материала</li><li>допускает существенные ошибки</li><li>неуверенно, с большими затруднениями решает практические задачи</li><li>не справляется с задачами самостоятельно</li></ul><p><strong>Оценка не выставляется</strong> обучающемуся, если:</p><ul><li>не явился на дифференцированный зачет</li><li>отказался от его сдачи</li><li>не знает программный материал</li></ul>',55)]))}const b=a(l,[["render",o]]);export{p as __pageData,b as default};
