import{_ as d,c as e,o as r,ag as a}from"./chunks/framework.D4Vqf8I7.js";const c=JSON.parse('{"title":"Дисциплины","description":"","frontmatter":{},"headers":[],"relativePath":"docs/disciplines/index.md","filePath":"docs/disciplines/index.md","lastUpdated":1753954622000}'),p={name:"docs/disciplines/index.md"};function o(s,t,h,i,n,l){return r(),e("div",null,t[0]||(t[0]=[a('<h1 id="дисциплины" tabindex="-1">Дисциплины <a class="header-anchor" href="#дисциплины" aria-label="Permalink to &quot;Дисциплины&quot;">​</a></h1><p>Все дисциплины взяты из специальности 09.02.06 Сетевое и системное администрирование ФГОС 2023 и 2016 года.</p><p>Хотя более взято из ФГОСа 16 года. Например, структура профессиональных модулей.</p><p>Так как я работал с модулем: Эксплуатация объектов сетевой инфраструктуры (по выбору), соответственно тут будет информация по этому варианту. В дальнейшем, возможно, будут создаваться и другие варианты.</p><p>Весь материал, представленный здесь, это личный вариант материалов для специальности 09.02.06. Он не претендует на оригинальность или что-то подобное. В процессе работы накапливается много материалов, и возможно, они будут полезны кому-либо.</p><p>Все материалы взяты из сети, книг и прочих источников. Надеюсь, копирайты меня не того... Буду стараться по максимуму прописывать все источники.</p><p>Материалы будут появляться очень медленно, ввиду того, что планируется их доработка/переработка с учётом требований ДЭ и реалиями импортозамещения в РФ. Упор будет на Альт Линкс.</p><p>Если кто-то желает присоединиться к разработке, можете создавать пул реквесты в Гитхаб или писать мне на почту.</p><p>Надеюсь, эти материалы помогут преподавателям и студентам в своём трудном и захватывающем пути освоения сетевого и системного администрирования.</p><p>Спасибо!</p><hr><p><a href="https://iksweb.ru/tools/generator-table-html/" target="_blank" rel="noreferrer">https://iksweb.ru/tools/generator-table-html/</a></p><p>Количество занятий в дисциплинах и часы усреднены, даже скорее приуменьшены.</p><p>Предупреждаю: я намерен в данной документации, по возможности, давать максимальное количество материалов на единицу времени. Будущий сетевой и системный администратор с первого курса должен понимать, что данное направление требует огромного количства знаний и умений. Можно годами учить лишь одну технологию, а в СиСА таких технологий сотни только в рамках четырёхлетнего курса, а в практике можно столкнуться и с тысячами. Поэтому, моё мнение, если студент не готов постоянно учиться, изучать материал, &quot;плавать&quot; в ИТ, то СиСА точно не для него. Единственный плюс, что в начале карьеры, всё таки, требуется знать много, но не сильно глубоко. В дальнейшем, уже на рабочем месте студенту придётся углубляться в технологии, которые применяются на его рабочем месте.</p><table tabindex="0"><thead><tr><th>Наименование дисциплины</th><th>Промежуточная аттестация</th><th>Всего часов</th><th>Самостоятельных работ</th><th>Лекций</th><th>Консультаций</th><th>Практических / лабораторных работ</th><th>Контрольные работы</th></tr></thead><tbody><tr><td>Основы алгоритмизации и программирования</td><td>ДЗ</td><td>96</td><td>16</td><td>22</td><td>8</td><td>ПР 44</td><td>6</td></tr><tr><td>Основы проектирования баз данных</td><td>Э 8</td><td>72</td><td>14</td><td>6</td><td>6</td><td>ПР 34</td><td>4</td></tr><tr><td>Архитектура аппаратных средств</td><td>Э 8</td><td>88</td><td>16</td><td>34</td><td>8</td><td>ПР 16</td><td>6</td></tr><tr><td>Операционные системы и среды</td><td>ДЗ</td><td>54</td><td>10</td><td>22</td><td>4</td><td>ПР 16</td><td>2</td></tr><tr><td>Технологии физического уровня передачи данных</td><td>ДЗ</td><td>58</td><td>8</td><td>8</td><td>6</td><td>ПР 32</td><td>4</td></tr><tr><td>Администрирование сетевых операционных систем</td><td>Э 8</td><td>260</td><td>36</td><td>132</td><td>12</td><td>ЛР 66</td><td>6</td></tr><tr><td>Программное обеспечение компьютерных сетей</td><td>Э 8</td><td>102</td><td>16</td><td>44</td><td>6</td><td>ЛР 24</td><td>4</td></tr><tr><td>Организация администрирования компьютерных систем</td><td>ДЗ</td><td>230</td><td>40</td><td>74</td><td>8</td><td>ЛР 102</td><td>6</td></tr><tr><td>Компьютерные сети</td><td>Э 8</td><td>258</td><td>34</td><td>64</td><td>16</td><td>ПР 106</td><td>12</td></tr><tr><td>Безопасность компьютерных сетей</td><td>Э 8</td><td>144</td><td>32</td><td>44</td><td>8</td><td>ЛР 48</td><td>4</td></tr></tbody></table>',15)]))}const b=d(p,[["render",o]]);export{c as __pageData,b as default};
