import{_ as a,c as d,o as r,ag as e}from"./chunks/framework.D4Vqf8I7.js";const u=JSON.parse('{"title":"Основы проектирования баз данных","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"docs/disciplines/opbd/index.md","filePath":"docs/disciplines/opbd/index.md","lastUpdated":1756474850000}'),l={name:"docs/disciplines/opbd/index.md"};function o(i,t,h,n,s,p){return r(),d("div",null,t[0]||(t[0]=[e('<h1 id="основы-проектирования-баз-данных" tabindex="-1">Основы проектирования баз данных <a class="header-anchor" href="#основы-проектирования-баз-данных" aria-label="Permalink to &quot;Основы проектирования баз данных&quot;">​</a></h1><h2 id="учебные-пособия" tabindex="-1">Учебные пособия <a class="header-anchor" href="#учебные-пособия" aria-label="Permalink to &quot;Учебные пособия&quot;">​</a></h2><p>Книги взяты с сайта <a href="https://postgrespro.ru/education/books" target="_blank" rel="noreferrer">Постгрес Про</a>:</p><p><a href="https://edu.postgrespro.ru/dbguide.pdf" target="_blank" rel="noreferrer">https://edu.postgrespro.ru/dbguide.pdf</a> Основы баз данных<br><a href="https://edu.postgrespro.ru/sql_primer.pdf" target="_blank" rel="noreferrer">https://edu.postgrespro.ru/sql_primer.pdf</a> В качестве основного учебного пособия</p><p><a href="https://study.urfu.ru/Aid/Publication/13604/1/Kara-Ushanov.pdf" target="_blank" rel="noreferrer">https://study.urfu.ru/Aid/Publication/13604/1/Kara-Ushanov.pdf</a> ER-МОДЕЛИ<br><a href="https://s.eduherald.ru/pdf/2020/2/20048.pdf" target="_blank" rel="noreferrer">https://s.eduherald.ru/pdf/2020/2/20048.pdf</a> ER-МОДЕЛИ<br><a href="https://ftp.postgresql.org/pub/pgadmin/pgadmin4/v9.6/docs/pgadmin4-9.6.pdf" target="_blank" rel="noreferrer">https://ftp.postgresql.org/pub/pgadmin/pgadmin4/v9.6/docs/pgadmin4-9.6.pdf</a> pgAdmin4</p><p>Ну очевидно, что тут будет постгрес в качесте реляционной) Почему? ДА потому что у МуСКЛ проприетарная лицензия. А в РФ сейчас в корпоративке единственный варик ФСТЭКовский - это либо чистый постгрес, либо ПостгресПро</p><h2 id="тематическии-план" tabindex="-1">Тематический план <a class="header-anchor" href="#тематическии-план" aria-label="Permalink to &quot;Тематический план&quot;">​</a></h2><table id="program-table"><thead><tr><th>Наименование разделов и тем</th><th>Содержание учебного материала и формы организации деятельности обучающихся</th><th>Уровень освоения</th><th>Объем часов</th><th>Осваиваемые элементы компетенций</th></tr><tr><th>1</th><th>2</th><th>3</th><th>4</th><th>5</th></tr></thead><tbody><tr><td colspan="5">Раздел 1. Теоретические основы систем управления базами данных</td></tr><tr><td rowspan="3">Тема 1.1 Основы теории СУБД</td><td colspan="3">Содержание учебного материала</td><td rowspan="3">ОК ПК ОК ПК ОК ПК</td></tr><tr><td>Классификация баз данных. Доступ к данным</td><td>1</td><td>2</td></tr><tr><td>Архитектура СУБД</td><td>1</td><td>2</td></tr><tr><td colspan="5">Раздел 2. Основы реляционных баз данных. PostgreSQL</td></tr><tr><td rowspan="5">Тема 2.1 Основы PostgreSQL и pgAdmin4</td><td colspan="3">Практические работы</td><td rowspan="5">ОК ПК ОК ПК ОК ПК</td></tr><tr><td>Основы реляционных БД. Основные операции с таблицами</td><td>2</td><td>2</td></tr><tr><td>Типы данных СУБД PostgreSQL</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Изучение pgAdmin4</td><td>3</td><td>2</td></tr><tr><td rowspan="15">Тема 2.2 DDL, DML, DCL и TCL</td><td colspan="3">Практические работы</td><td rowspan="15">ОК ПК ОК ПК ОК ПК</td></tr><tr><td>Основы языка определения данных</td><td>2</td><td>2</td></tr><tr><td>Запросы</td><td>2</td><td>2</td></tr><tr><td>Запросы</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Написание простых запросов</td><td>3</td><td>2</td></tr><tr><td colspan="3">Практические работы</td></tr><tr><td>Изменение данных</td><td>2</td><td>2</td></tr><tr><td>Изменение данных</td><td>2</td><td>2</td></tr><tr><td>Индексы</td><td>2</td><td>2</td></tr><tr><td>Индексы</td><td>2</td><td>2</td></tr><tr><td>Транзакции</td><td>2</td><td>2</td></tr><tr><td>Транзакции</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Транзакции</td><td>3</td><td>2</td></tr><tr><td rowspan="6">Тема 2.3 ER-диаграммы в нотации IDEF1X</td><td colspan="3">Практические работы</td><td rowspan="6">ОК ПК ОК ПК ОК ПК</td></tr><tr><td>Синтаксис ER-модели в нотации IDEF1X</td><td>2</td><td>2</td></tr><tr><td>Построение информационной модели</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Построение информационной модели</td><td>3</td><td>2</td></tr><tr><td colspan="3">Контрольная работа: Реляционные базы данных</td></tr><tr><td colspan="5">Раздел 3. Нереляционные базы данных</td></tr><tr><td rowspan="19">Тема 3.1 Нереляционные БД</td><td colspan="3">Содержание учебного материала</td><td rowspan="19">ОК ПК ОК ПК ОК ПК</td></tr><tr><td>Теоретические основые нереляционных баз данных</td><td>1</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Базы данных типа ключ-значение. Резидентная СУБД Valkey</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Работа с СУБД Valkey</td><td>3</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Документоориентированная СУБД MongoDB</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Работа с СУБД MongoDB</td><td>3</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Колоночная СУБД Cassandra</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Работа с СУБД Cassandra</td><td>3</td><td>2</td></tr><tr><td colspan="3">Практическая работа</td></tr><tr><td>Графовая СУБД Neo4j</td><td>2</td><td>2</td></tr><tr><td colspan="3">Самостоятельная работа</td></tr><tr><td>Работа с СУБД Neo4j</td><td>3</td><td>2</td></tr><tr><td colspan="3">Контрольная работа: Нереляционные базы данных</td></tr></tbody><tfoot><tr><td colspan="3">Консультации</td><td>6</td><td> </td></tr><tr><td colspan="3">Экзамен</td><td>8</td><td> </td></tr><tr><td colspan="3">Всего</td><td>72</td><td> </td></tr></tfoot></table><h2 id="контрольно-оценочные-средства-промежуточнои-аттестации" tabindex="-1">Контрольно-оценочные средства промежуточной аттестации <a class="header-anchor" href="#контрольно-оценочные-средства-промежуточнои-аттестации" aria-label="Permalink to &quot;Контрольно-оценочные средства промежуточной аттестации&quot;">​</a></h2><p>Время выполнения: 40 минут. Теоретичски, можно проводить в две смены. С учётом проверки и подготовки рабочих мест, займет две пары.</p><p>Для каждого студента заранее готовится две чистые обновленные ВМ и готовый Obsidian. Репозиторий локальный.</p><p>Доступа в интернет быть не должно.</p><p>В качесте результата: Обсидиан с отчётом о проделанной работе (со скриншотами) и ответом на теоретический вопрос. Подключаете Vault удаленный, который полностью доступен преподавателю, а студентам только личный, например, с помощью локального Git-репозитория</p><h3 id="вопросы-для-подготовки" tabindex="-1">Вопросы для подготовки: <a class="header-anchor" href="#вопросы-для-подготовки" aria-label="Permalink to &quot;Вопросы для подготовки:&quot;">​</a></h3><ol><li></li><li></li></ol><h3 id="форма-промежуточнои-аттестации-экзамен-по-билетам" tabindex="-1">Форма промежуточной аттестации: Экзамен (по билетам) <a class="header-anchor" href="#форма-промежуточнои-аттестации-экзамен-по-билетам" aria-label="Permalink to &quot;Форма промежуточной аттестации: Экзамен (по билетам)&quot;">​</a></h3><h4 id="билет-1" tabindex="-1">Билет 1 <a class="header-anchor" href="#билет-1" aria-label="Permalink to &quot;Билет 1&quot;">​</a></h4><ol><li>Теоретичский вопрос о реляционной или нереляционной бд</li><li>Поднять реляционную бд. Наполнить согласно билету. Написать транзакцию.</li><li>Поднять нереляционную бд. Наполнить согласно билету. Написать запрос.</li></ol><h4 id="билет-2" tabindex="-1">Билет 2 <a class="header-anchor" href="#билет-2" aria-label="Permalink to &quot;Билет 2&quot;">​</a></h4><h4 id="билет-3" tabindex="-1">Билет 3 <a class="header-anchor" href="#билет-3" aria-label="Permalink to &quot;Билет 3&quot;">​</a></h4><h4 id="билет-4" tabindex="-1">Билет 4 <a class="header-anchor" href="#билет-4" aria-label="Permalink to &quot;Билет 4&quot;">​</a></h4><h4 id="билет-5" tabindex="-1">Билет 5 <a class="header-anchor" href="#билет-5" aria-label="Permalink to &quot;Билет 5&quot;">​</a></h4><h4 id="билет-6" tabindex="-1">Билет 6 <a class="header-anchor" href="#билет-6" aria-label="Permalink to &quot;Билет 6&quot;">​</a></h4><h4 id="билет-7" tabindex="-1">Билет 7 <a class="header-anchor" href="#билет-7" aria-label="Permalink to &quot;Билет 7&quot;">​</a></h4><h4 id="билет-8" tabindex="-1">Билет 8 <a class="header-anchor" href="#билет-8" aria-label="Permalink to &quot;Билет 8&quot;">​</a></h4><h4 id="билет-9" tabindex="-1">Билет 9 <a class="header-anchor" href="#билет-9" aria-label="Permalink to &quot;Билет 9&quot;">​</a></h4><h4 id="билет-10" tabindex="-1">Билет 10 <a class="header-anchor" href="#билет-10" aria-label="Permalink to &quot;Билет 10&quot;">​</a></h4><h4 id="билет-11" tabindex="-1">Билет 11 <a class="header-anchor" href="#билет-11" aria-label="Permalink to &quot;Билет 11&quot;">​</a></h4><h4 id="билет-12" tabindex="-1">Билет 12 <a class="header-anchor" href="#билет-12" aria-label="Permalink to &quot;Билет 12&quot;">​</a></h4><h4 id="билет-13" tabindex="-1">Билет 13 <a class="header-anchor" href="#билет-13" aria-label="Permalink to &quot;Билет 13&quot;">​</a></h4><h4 id="билет-14" tabindex="-1">Билет 14 <a class="header-anchor" href="#билет-14" aria-label="Permalink to &quot;Билет 14&quot;">​</a></h4><h4 id="билет-15" tabindex="-1">Билет 15 <a class="header-anchor" href="#билет-15" aria-label="Permalink to &quot;Билет 15&quot;">​</a></h4><h4 id="билет-16" tabindex="-1">Билет 16 <a class="header-anchor" href="#билет-16" aria-label="Permalink to &quot;Билет 16&quot;">​</a></h4><h4 id="билет-17" tabindex="-1">Билет 17 <a class="header-anchor" href="#билет-17" aria-label="Permalink to &quot;Билет 17&quot;">​</a></h4><h4 id="билет-18" tabindex="-1">Билет 18 <a class="header-anchor" href="#билет-18" aria-label="Permalink to &quot;Билет 18&quot;">​</a></h4><h4 id="билет-19" tabindex="-1">Билет 19 <a class="header-anchor" href="#билет-19" aria-label="Permalink to &quot;Билет 19&quot;">​</a></h4><h4 id="билет-20" tabindex="-1">Билет 20 <a class="header-anchor" href="#билет-20" aria-label="Permalink to &quot;Билет 20&quot;">​</a></h4><h4 id="билет-21" tabindex="-1">Билет 21 <a class="header-anchor" href="#билет-21" aria-label="Permalink to &quot;Билет 21&quot;">​</a></h4><h4 id="билет-22" tabindex="-1">Билет 22 <a class="header-anchor" href="#билет-22" aria-label="Permalink to &quot;Билет 22&quot;">​</a></h4><h4 id="билет-23" tabindex="-1">Билет 23 <a class="header-anchor" href="#билет-23" aria-label="Permalink to &quot;Билет 23&quot;">​</a></h4><h4 id="билет-24" tabindex="-1">Билет 24 <a class="header-anchor" href="#билет-24" aria-label="Permalink to &quot;Билет 24&quot;">​</a></h4><h4 id="билет-25" tabindex="-1">Билет 25 <a class="header-anchor" href="#билет-25" aria-label="Permalink to &quot;Билет 25&quot;">​</a></h4><h4 id="билет-26" tabindex="-1">Билет 26 <a class="header-anchor" href="#билет-26" aria-label="Permalink to &quot;Билет 26&quot;">​</a></h4><h4 id="билет-27" tabindex="-1">Билет 27 <a class="header-anchor" href="#билет-27" aria-label="Permalink to &quot;Билет 27&quot;">​</a></h4><h4 id="билет-28" tabindex="-1">Билет 28 <a class="header-anchor" href="#билет-28" aria-label="Permalink to &quot;Билет 28&quot;">​</a></h4><h4 id="билет-29" tabindex="-1">Билет 29 <a class="header-anchor" href="#билет-29" aria-label="Permalink to &quot;Билет 29&quot;">​</a></h4><h4 id="билет-30" tabindex="-1">Билет 30 <a class="header-anchor" href="#билет-30" aria-label="Permalink to &quot;Билет 30&quot;">​</a></h4><h4 id="билет-31" tabindex="-1">Билет 31 <a class="header-anchor" href="#билет-31" aria-label="Permalink to &quot;Билет 31&quot;">​</a></h4><h4 id="билет-32" tabindex="-1">Билет 32 <a class="header-anchor" href="#билет-32" aria-label="Permalink to &quot;Билет 32&quot;">​</a></h4><h3 id="критерии-оценки" tabindex="-1">Критерии оценки <a class="header-anchor" href="#критерии-оценки" aria-label="Permalink to &quot;Критерии оценки&quot;">​</a></h3><p><strong>Оценка «отлично»</strong> выставляется обучающемуся, если:</p><ul><li>глубоко и прочно усвоил программный материал курса</li><li>исчерпывающе, последовательно, четко и логически стройно его излагает</li><li>умеет тесно увязывать теорию с практикой</li><li>свободно справляется с задачами и вопросами</li><li>не затрудняется с ответами при видоизменении заданий</li><li>правильно обосновывает принятые решения</li></ul><p><strong>Оценка «хорошо»</strong> выставляется обучающемуся, если:</p><ul><li>твердо знает материал курса</li><li>грамотно и по существу излагает его</li><li>не допускает существенных неточностей в ответе на вопрос</li><li>правильно применяет теоретические положения при решении практических вопросов и задач</li><li>владеет необходимыми навыками и приемами их выполнения</li></ul><p><strong>Оценка «удовлетворительно»</strong> выставляется обучающемуся, если:</p><ul><li>имеет знания только основного материала</li><li>не усвоил деталей материала</li><li>допускает неточности и недостаточно правильные формулировки</li><li>нарушает логическую последовательность в изложении</li><li>испытывает затруднения при выполнении задач</li></ul><p><strong>Оценка «неудовлетворительно»</strong> выставляется обучающемуся, если:</p><ul><li>не знает значительной части программного материала</li><li>допускает существенные ошибки</li><li>неуверенно, с большими затруднениями решает практические задачи</li><li>не справляется с задачами самостоятельно</li></ul><p><strong>Оценка не выставляется</strong> обучающемуся, если:</p><ul><li>не явился на дифференцированный зачет</li><li>отказался от его сдачи</li><li>не знает программный материал</li></ul>',60)]))}const b=a(l,[["render",o]]);export{u as __pageData,b as default};
