import{_ as e,c as s,o as a,ag as p}from"./chunks/framework.D4Vqf8I7.js";const r="/wiki/assets/Networkwithquadro.CES3xKNY.png",u=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/disciplines/pm2/mdk23/index.md","filePath":"docs/disciplines/pm2/mdk23/index.md","lastUpdated":1754578927000}'),i={name:"docs/disciplines/pm2/mdk23/index.md"};function n(o,t,d,c,l,_){return a(),s("div",null,t[0]||(t[0]=[p('<p>Эктив Директори, самба, фриипа</p><p>Новейшая тема: утилита для синхронизации сисвол <a href="https://altlinux.space/alt-domain/svsync" target="_blank" rel="noreferrer">https://altlinux.space/alt-domain/svsync</a> протестировали инструмент, развернув на PVE стенд следующей конфигурации:</p><p>2 сайта, для каждого сайта определена своя подсеть.</p><p>В первом сайте находится PDC(dc1), альт сервер с КД (dc2) и windows server 2022 с КД (dcwind).</p><p>Во втором сайте находится 2 альт сервера с КД(dc3 и dc4), один из которых ISTG.</p><p>Иллюстративное изображение схемы стенда прикрепляю во вложении.</p><p>Ожидаемое поведение - корректный выбор для каждого типа контроллера источника репликации, репликация в нужное место. Репликацию можно считать корректной, если после нее sysvol &quot;принимающего&quot; контроллера идентичен sysvol &quot;отдающего&quot; контроллера.</p><p>В ходе тестирования, было проверено, что для PDC, ISTG и контроллеров без fsmo ролей выбирается верный источник для синхронизации.</p><p>Процесс тестирования и базовый кейс для тестировщиков сервиса описал в задаче <a href="https://my.basealt.space/issues/185757" target="_blank" rel="noreferrer">https://my.basealt.space/issues/185757</a></p><p><img src="'+r+'" alt="alt text"></p>',10)]))}const f=e(i,[["render",n]]);export{u as __pageData,f as default};
