import{_ as a,c as i,o as e,ag as t}from"./chunks/framework.D4Vqf8I7.js";const h="/wiki/assets/verify-cheks.DcbccE3t.png",u=JSON.parse('{"title":"verefy-checksum","description":"","frontmatter":{},"headers":[],"relativePath":"docs/wi/altsp/16.checksum.md","filePath":"docs/wi/altsp/16.checksum.md","lastUpdated":1754656054000}'),l={name:"docs/wi/altsp/16.checksum.md"};function p(n,s,c,k,d,r){return e(),i("div",null,s[0]||(s[0]=[t('<p>Операционная система должна предоставлять инструмент проверки контрольных сумм неизменяемых файлов установленных пакетов как при начальной установке, так и после получения обновлений. Вендор должен обеспечивать доступ к обновлениям и контрольным суммам неизменяемых файлов пакетов в них.</p><hr><p>[[ЛКНВ.11100-01 99 03 Руководство по комплексу средств защиты.pdf]], стр 129-133</p><p>Графическая утилита: gtkhash</p><h1 id="verefy-checksum" tabindex="-1">verefy-checksum <a class="header-anchor" href="#verefy-checksum" aria-label="Permalink to &quot;verefy-checksum&quot;">​</a></h1><p>Утилита verify-checksums предназначена для проверки установленных пакетов rpm</p><p>Для запуска утилиты ей необходимо указать путь к каталогу (/chksum), содержащему файлы в формате .chksum. Эти файлы должны содержать информацию из исходных пакетов rpm. Пофайловые отчеты подсчета контрольных сумм неизменяемых файлов дистрибутива, представлены на установочном компакт-диске каждого варианта исполнения соответствующей процессорной архитектуры в корневой директории /chksum. Для успешной проверки файлов программа должна запускаться с правами суперпользователя.</p><p>Пример (для x86_64): - выполнить проверку подлинности файлов ОС:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">verify-checksums</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --dir</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /media/ALTLinux/chksum/c10f2</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --dir</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /media/ALTLinux/chksum/c10f2/noarch</span></span></code></pre></div><p><img src="'+h+'" alt=""></p><h1 id="alt-chksum" tabindex="-1">alt-chksum <a class="header-anchor" href="#alt-chksum" aria-label="Permalink to &quot;alt-chksum&quot;">​</a></h1><p>Утилита alt-chksum проверяет контрольные суммы файлов, вызывая verify-checksums.</p><p>Пример:</p><ul><li>отобразить предварительно настроенный пронумерованный набор URL-адресов:</li></ul><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">alt-chksum</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> url</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> \\?</span></span></code></pre></div><ul><li>отобразить список веток для выбранного URL:</li></ul><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">alt-chksum</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> add</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> \\?</span></span></code></pre></div><ul><li>добавить контрольные суммы в зависимости от архитектуры, в примере для c10f2/x86_64:</li></ul><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">alt-chksum</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> add</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> c10f2/x86_64</span></span></code></pre></div><ul><li>просмотреть список веток, которые в данный момент проверены:</li></ul><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">alt-chksum</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> show</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> \\?</span></span></code></pre></div><ul><li>проверить контрольные суммы:</li></ul><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">alt-chksum</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> verify</span></span></code></pre></div>',23)]))}const g=a(l,[["render",p]]);export{u as __pageData,g as default};
