import{_ as s,c as t,o as a,a3 as e}from"./chunks/framework.o29YdDde.js";const p="/wiki/assets/Pastedimage20250818101434.CupsXzFd.png",l="/wiki/assets/Pastedimage20250818101453.DfaMrKXD.png",o="/wiki/assets/Pastedimage20250818101505.HpjMZ4me.png",n="/wiki/assets/Pastedimage20250818101527.BgVspOqy.png",r="/wiki/assets/Pastedimage20250818101543.5DnM-H-o.png",g="/wiki/assets/Pastedimage20250818101605.BmU9i8yX.png",c="/wiki/assets/Pastedimage20250818101631.BCWN-yPr.png",d="/wiki/assets/Pastedimage20250818101653.BcBuyfcn.png",k="/wiki/assets/Pastedimage20250818101801.CcCfjye4.png",m="/wiki/assets/Pastedimage20250818101814.DVc5ik6h.png",h="/wiki/assets/Pastedimage20250818101925.D1A_r1-X.png",u="/wiki/assets/Pastedimage20250818102035.Bm4vZ3Qz.png",_="/wiki/assets/Pastedimage20250818102054.DpfQWXIq.png",b="/wiki/assets/Pastedimage20250818102725.BZ-AKPix.png",q="/wiki/assets/Pastedimage20250818102819.D7Dm65Ho.png",F="/wiki/assets/Pastedimage20250818105633.DUHIOkGb.png",y="/wiki/assets/Pastedimage20250818103145.CzEJSP_h.png",E="/wiki/assets/Pastedimage20250818103202.YGtwOQpG.png",w="/wiki/assets/Pastedimage20250818103212.COc_HkJq.png",P="/wiki/assets/Pastedimage20250818103226.BPD1ZVxi.png",C="/wiki/assets/Pastedimage20250818103303.BDEknd3E.png",B="/wiki/assets/Pastedimage20250818103439.DEZmyy-b.png",f="/wiki/assets/Pastedimage20250818103508.B2-kNpqc.png",D="/wiki/assets/Pastedimage20250818103748.w9hti8r6.png",I=JSON.parse('{"title":"Git и Obsidian","description":"","frontmatter":{},"headers":[],"relativePath":"docs/wi/git-obsidian.md","filePath":"docs/wi/git-obsidian.md","lastUpdated":1755593728000}'),v={name:"docs/wi/git-obsidian.md"};function A(O,i,x,N,T,V){return a(),t("div",null,i[0]||(i[0]=[e('<h1 id="git-и-obsidian" tabindex="-1">Git и Obsidian <a class="header-anchor" href="#git-и-obsidian" aria-label="Permalink to &quot;Git и Obsidian&quot;">​</a></h1><p><img src="https://habrastorage.org/r/w1560/getpro/habr/upload_files/48a/fd9/2b8/48afd92b8f85bbe3d9c96cb54ae059e4.png" alt=""></p><p>Очевидно, что этот гайд взят с <a href="https://habr.com/ru/articles/843288/" target="_blank" rel="noreferrer">https://habr.com/ru/articles/843288/</a> Картинку нужно переделать под себя</p><p>Это инструкция по настройке git-репозитория, на примере<code>gitlab.basealt.space</code>, в качестве удалённого хранилища Obsidian.</p><ol><li><p>Переходим и авторизуемся на сайте: gitlab.basealt.space <img src="'+p+'" alt=""></p></li><li><p>Слева вверху нажимаем на &quot;Плюс&quot; и выбираем &quot;New project/repository&quot; <img src="'+l+'" alt=""></p></li><li><p>Выбираем &quot;Create blank project&quot; <img src="'+o+'" alt=""></p></li><li><p>Заполняем имя проекта и ставим чек напротив пункта &quot;Initialize repository with a README&quot;, после чего нажимаем &quot;Create project&quot; <img src="'+n+'" alt=""></p></li><li><p>Оказываемся в созданном репозитории. Сверху нажимаем на &quot;Плюс&quot; и выбираем &quot;New file&quot; <img src="'+r+'" alt=""></p></li><li><p>Название файла <code>.gitignore</code>, содержимое: <code>.obsidian</code>. Нажимаем &quot;Commin changes&quot; <img src="'+g+'" alt=""></p></li><li><p>Вновь нажимаем &quot;Commin changes&quot; <img src="'+c+'" alt=""></p></li><li><p>Проверяем созданный файл. <img src="'+d+'" alt=""></p></li></ol><blockquote><p>Комментарий: файл <code>.gitignore</code> нужен, чтобы не синхронизировать папку <code>.obsidian</code>, в которой находятся локальные настройки (конфликтуют на разных устройствах при синхронизации). Если этого не сделать — вся схема перестанет работать.</p></blockquote><ol start="9"><li><p>Слева вверху нажимаем на значок пользователя и выбираем пункт &quot;Preferences&quot; <img src="'+k+'" alt=""></p></li><li><p>Переходим в раздел &quot;Access tokens&quot; и нажимаем на &quot;Add new token&quot; <img src="'+m+'" alt=""></p></li><li><p>Указываем название токена, дату истечения срока годности, а также ставим чек напротив пунктов как на скриншотах: <img src="'+h+'" alt=""><img src="'+u+'" alt=""></p></li><li><p>Нажимаем на &quot;Create token&quot;. Копируем его. Это единственный раз, когда он будет доступен. <img src="'+_+'" alt=""></p></li><li><p>Переходим в терминал. Создаём директорию, в которой будет располагаться наш Vault, например:</p></li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">mkdir</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> vaults</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> &amp;&amp; </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">cd</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> vaults</span></span></code></pre></div><ol start="14"><li>Далее необходимо произвести клонирование репозитория, используя ранее созданный токен:</li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> clone</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> https://oauth2:</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&lt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">access_toke</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">n</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&gt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">@gitlab.basealt.space/</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&lt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">usernam</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">e</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&gt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">/</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&lt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">rep</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">o</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&gt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">.git</span></span></code></pre></div><ol start="15"><li><p>Открываем Obsidian, выбираем пункт &quot;Open folder as vault&quot; и выбираем склонированный репозиторий. <img src="'+b+'" alt=""></p></li><li><p>На данный момент, в директории располагается только файл <code>README.md</code>. <img src="'+q+'" alt=""></p></li><li><p>Слева внизу нажимаем на шестерёнку и выбираем раздел &quot;Community plugin&quot;. Далее нажимаем &quot;Turn on community plugins&quot; <img src="'+F+'" alt=""></p></li><li><p>Нажимаем &quot;Browse&quot; <img src="'+y+'" alt=""></p></li><li><p>В поиске ищем <code>git</code><img src="'+E+'" alt=""></p></li><li><p>Устанавливаем плагин нажатием на &quot;Install&quot; <img src="'+w+'" alt=""></p></li><li><p>Активируем плагин нажатием на &quot;Enable&quot; <img src="'+P+'" alt=""></p></li><li><p>Далее необходимо настроить плагин. Нажимаем на &quot;Options&quot; <img src="'+C+'" alt=""></p></li><li><p>Устанавливаем следующие параметры:</p></li></ol><ul><li><p>Auto commit-and-sync interval: 1</p></li><li><p>Auto commit-and-sync after stopping file edits: yes <img src="'+B+'" alt=""></p></li><li><p>Pull on startup: yes <img src="'+f+'" alt=""></p></li></ul><ol start="24"><li>Теперь, если произвести какие-нибудь действия с файлами, то в течение минуты все изменения будут отправлены на gitlab <img src="'+D+'" alt=""></li></ol>',13)]))}const S=s(v,[["render",A]]);export{I as __pageData,S as default};
