import{_ as a,c as s,o as t,ag as e}from"./chunks/framework.D4Vqf8I7.js";const o="/wiki/assets/logism.CKItcEOZ.png",k=JSON.parse('{"title":"Logism","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"docs/wi/logism.md","filePath":"docs/wi/logism.md","lastUpdated":1759236490000}'),l={name:"docs/wi/logism.md"};function n(p,i,h,r,d,g){return t(),s("div",null,i[0]||(i[0]=[e('<h1 id="logism" tabindex="-1">Logism <a class="header-anchor" href="#logism" aria-label="Permalink to &quot;Logism&quot;">​</a></h1><p><img src="https://github.com/logisim-evolution/logisim-evolution/raw/main/docs/img/logisim-evolution-logo.png" alt=""></p><p>Logisim Evolution представляет собой современное, активно развиваемое ответвление оригинального проекта Logisim, разработка которого официально прекращена. В отличие от классического Logisim, Evolution предлагает значительно улучшенную производительность и стабильность, особенно при работе со сложными схемами, благодаря глубокой оптимизации кода. Проект отличается современным пользовательским интерфейсом, включающим поддержку вкладок для нескольких схем и тем оформления (светлая/тёмная).</p><p>Logisim Evolution существенно расширяет функциональность, предлагая богатый набор дополнительных компонентов (разнообразные триггеры, АЛУ, расширенные регистры, генераторы слов, улучшенные инструменты рисования) и расширенные возможности анализа схем. При этом он сохраняет высокую совместимость с файлами оригинального Logisim. Будучи активно поддерживаемым сообществом, Logisim Evolution является рекомендуемой заменой устаревшему классическому Logisim для всех задач проектирования цифровых схем.</p><div class="tip custom-block"><p class="custom-block-title">Совет</p><p>Очевидно, что буду рекомендовать устанавливать именно Logisim Evolution и именно из rpm-пакета.</p></div><h2 id="установка" tabindex="-1">Установка <a class="header-anchor" href="#установка" aria-label="Permalink to &quot;Установка&quot;">​</a></h2><p>Скачайте <a href="https://sourceforge.net/projects/circuit/" target="_blank" rel="noreferrer">Logism</a> или <a href="https://github.com/logisim-evolution/logisim-evolution" target="_blank" rel="noreferrer">Logism Evolution</a>. Выбирайте что скачивать в зависимости от вариантов установки.</p><h3 id="_1-rpm-пакет-только-evolution" tabindex="-1">1. rpm-пакет - только evolution <a class="header-anchor" href="#_1-rpm-пакет-только-evolution" aria-label="Permalink to &quot;1. rpm-пакет - только evolution&quot;">​</a></h3><p>Устанавливается без перепаковки, как через графику, так и в терминале:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">apt-get</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ./logisim-evolution-3.9.0-1.x86_64.rpm</span></span></code></pre></div><h3 id="_2-flatpak-только-evolution" tabindex="-1">2. Flatpak - только evolution <a class="header-anchor" href="#_2-flatpak-только-evolution" aria-label="Permalink to &quot;2. Flatpak - только evolution&quot;">​</a></h3><p>Тут вообще отдельно ничего скачивать не нужно:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">flatpak</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> flathub</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> com.github.reds.LogisimEvolution</span></span></code></pre></div><h3 id="_3-чистыи-jar-оригинал-и-evolution" tabindex="-1">3. Чистый jar - оригинал и evolution <a class="header-anchor" href="#_3-чистыи-jar-оригинал-и-evolution" aria-label="Permalink to &quot;3. Чистый jar - оригинал и evolution&quot;">​</a></h3><p>Тут уже необходимо всё делать руками.</p><p>Установите OpenJDK:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">apt-get</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> java-21-openjdk</span></span></code></pre></div><p>Запустите Logism:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">java</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -jar</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> logism-generic-2.7.1.jar</span></span></code></pre></div><p>Можно создать пункт в меню (пример для KDE): <img src="'+o+'" alt="alt text"></p>',20)]))}const u=a(l,[["render",n]]);export{k as __pageData,u as default};
