import{_ as t,c as o,o as e,ag as a}from"./chunks/framework.D4Vqf8I7.js";const g=JSON.parse('{"title":"n8n","description":"","frontmatter":{},"headers":[],"relativePath":"docs/wi/n8n.md","filePath":"docs/wi/n8n.md","lastUpdated":1761036841000}'),r={name:"docs/wi/n8n.md"};function s(i,n,c,p,d,l){return e(),o("div",null,n[0]||(n[0]=[a('<h1 id="n8n" tabindex="-1">n8n <a class="header-anchor" href="#n8n" aria-label="Permalink to &quot;n8n&quot;">​</a></h1><p><img src="https://n8niostorageaccount.blob.core.windows.net/n8nio-strapi-blobs-prod/assets/Agent_chat_818315ae64.webp" alt=""></p><p><strong>n8n</strong> — это открытая (open-source) платформа для автоматизации рабочих процессов, основанная на концепции «низкокодового» (low-code) программирования. С её помощью пользователи могут визуально создавать так называемые <strong>workflow</strong> (рабочие процессы), соединяя различные сервисы и приложения через готовые интеграции — <strong>ноды</strong> (nodes). Каждая нода представляет собой действие в определённом сервисе: отправка письма через Gmail, создание задачи в Trello, запрос к базе данных, вызов API и т.д. n8n особенно популярен среди разработчиков и технических пользователей, так как сочетает простоту визуального редактора с гибкостью кастомизации — при необходимости можно писать собственные функции на JavaScript.</p><p>Платформа n8n может работать как локально на собственном сервере (что обеспечивает полный контроль над данными и безопасность), так и в облаке (через официальный хостинг или сторонние провайдеры). Она поддерживает сотни интеграций с популярными инструментами — от Slack и Notion до баз данных и кастомных REST API. Благодаря своей модульной архитектуре, n8n легко расширяется и адаптируется под сложные бизнес-процессы, что делает его мощной альтернативой таким платформам, как Zapier или Make (Integromat), особенно для тех, кто ценит приватность, гибкость и открытый исходный код.</p><p><a href="https://docs.n8n.io/hosting/" target="_blank" rel="noreferrer">https://docs.n8n.io/hosting/</a></p>',5)]))}const h=t(r,[["render",s]]);export{g as __pageData,h as default};
