import{_ as t,c as a,o as s,j as e,a as o}from"./chunks/framework.D4Vqf8I7.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/wi/obfsproxy.md","filePath":"docs/wi/obfsproxy.md","lastUpdated":1764663320000}'),l={name:"docs/wi/obfsproxy.md"};function p(n,r,c,f,d,i){return s(),a("div",null,r[0]||(r[0]=[e("p",null,[e("a",{href:"https://habr.com/ru/articles/251569/",target:"_blank",rel:"noreferrer"},"https://habr.com/ru/articles/251569/")],-1),e("p",null,[o("Альт Линукс пакет "),e("a",{href:"https://packages.altlinux.org/ru/p11/srpms/obfs4/",target:"_blank",rel:"noreferrer"},"obfs4")],-1),e("p",null,"Обфускация — это техника сокрытия или искажения данных, метаданных или сетевого трафика с целью затруднить их анализ, распознавание или перехват злоумышленниками, не изменяя при этом функциональности системы. Она применяется, например, для маскировки вредоносного кода в сетевых пакетах, скрытия реальных адресов или портов в прокси- и VPN-соединениях, а также для обхода систем детекции на основе сигнатур (например, IDS/IPS), заставляя анализаторы трафика сталкиваться с неоднозначными или запутанными шаблонами. Обфускация не является шифрованием, но может дополнять его, повышая сложность обратного инжиниринга и снижая эффективность автоматизированных атак, при этом сохраняя возможность легитимного взаимодействия между узлами сети.",-1),e("p",null,"Пакет obfs4 — это программное обеспечение, предназначенное для обфускации сетевого трафика с целью защиты от анализа и блокировки со стороны инфраструктур, применяющих глубокую проверку пакетов (DPI). Он работает за счёт маскировки реального содержания соединения под стандартный шифрованный трафик, такой как HTTPS, делая его статистически неотличимым от обычного защищённого обмена данными. Протокол использует криптографически безопасные методы для динамического изменения структуры потока данных, что предотвращает выявление характерных сигнатур и повышает устойчивость к цензуре или ограничению доступа в сети. Obfs4 часто применяется в сценариях, где требуется обход сетевых ограничений без использования явно подозрительных протоколов.",-1)]))}const m=t(l,[["render",p]]);export{b as __pageData,m as default};
