import{_ as o,c as t,o as r,ag as a}from"./chunks/framework.D4Vqf8I7.js";const j=JSON.parse('{"title":"OpenProject","description":"","frontmatter":{},"headers":[],"relativePath":"docs/wi/open-project.md","filePath":"docs/wi/open-project.md","lastUpdated":1760542433000}'),n={name:"docs/wi/open-project.md"};function p(c,e,s,i,l,d){return r(),t("div",null,e[0]||(e[0]=[a('<h1 id="openproject" tabindex="-1">OpenProject <a class="header-anchor" href="#openproject" aria-label="Permalink to &quot;OpenProject&quot;">​</a></h1><p><img src="https://www.openproject.org/assets/images/cover/openproject-home-5ab8cc23.jpg" alt=""></p><p><strong>OpenProject</strong> — это мощная система управления проектами с открытым исходным кодом, предназначенная для планирования, отслеживания задач, совместной работы и контроля сроков выполнения работ. Она предоставляет широкий набор функций, включая управление задачами и подзадачами, диаграммы Ганта, системы контроля версий (интеграция с Git), управление требованиями, вики, форумы, календари, отслеживание времени и гибкие настройки прав доступа. OpenProject особенно популярен в ИТ-компаниях, инженерных и консалтинговых фирмах, где требуется прозрачность, структурированность и соответствие стандартам управления проектами.</p><p>Платформа доступна как в виде <strong>онлайн-сервиса</strong> (SaaS), так и для <strong>локальной установки</strong> на собственные серверы, что делает её привлекательной для организаций, заботящихся о конфиденциальности и безопасности данных. OpenProject поддерживает как классические (водопадные), так и гибкие (Agile, Scrum) методологии управления проектами, а его интерфейс локализован на множество языков, включая русский. Благодаря открытому исходному коду, гибкости настройки и активному сообществу, OpenProject считается одной из лучших open-source альтернатив таким решениям, как Jira, Microsoft Project или Trello — особенно для команд, которым нужна комплексная, но независимая система управления проектами.</p><p><a href="https://www.openproject.org/docs/installation-and-operations/" target="_blank" rel="noreferrer">https://www.openproject.org/docs/installation-and-operations/</a></p>',5)]))}const g=o(n,[["render",p]]);export{j as __pageData,g as default};
