import{_ as a,c as e,o as r,ag as s}from"./chunks/framework.D4Vqf8I7.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"docs/wi/tricks.md","filePath":"docs/wi/tricks.md","lastUpdated":1758023321000}'),i={name:"docs/wi/tricks.md"};function o(l,t,p,c,d,n){return r(),e("div",null,t[0]||(t[0]=[s('<h2 id="проброс-репозитория-в-закрытыи-контур" tabindex="-1">Проброс репозитория в закрытый контур <a class="header-anchor" href="#проброс-репозитория-в-закрытыи-контур" aria-label="Permalink to &quot;Проброс репозитория в закрытый контур&quot;">​</a></h2><ol><li>Разворачивают локальный репо.</li><li>Далее с делают копию на патаскун нашего репо.</li><li>Проверяют патаскун на антивирусе (требования ФСТЭК) .</li><li>С проверенного патаскуна синхронизируют с локальным репо в закрытом контуре. Синхранизации наш репо- патаскун-локальный репо в закрытом контуре делают через rsync. Это стандартная процедура для закрытого контура.</li></ol><p>Второй вариант. Если используются сертифицированные версии МЭ. И есть доступ через них в закрытый контур (Физически не разделены) То регулятор разрешает так скажем однустороннюю связь по определенным портам. Тогда Локальный репо в открытой их сети. Проверка этого репо чере антивирус. И синхронизациия уже этого репо с репо в закрытой сети через сертифицированный МЭ.</p><p>Обязательно в любом случае повторно проверяется перед использованием репо в закрытой сети антивирусом. Желательно другим. То есть в KAV в открытой (не сертифицированной сети) или патаскун. В Закрытой Drweb. Или Наоборот. Но это рекомендация ФСТЭК. Можно и одним но опять не рекомендовано. Но наказание за это не предусмотрено. Максимум рекомендация ФСТЭК при проверке ОИ.</p><h2 id="чем-можно-проверить-диск-в-альт-линукс" tabindex="-1">Чем можно проверить диск в Альт Линукс <a class="header-anchor" href="#чем-можно-проверить-диск-в-альт-линукс" aria-label="Permalink to &quot;Чем можно проверить диск в Альт Линукс&quot;">​</a></h2><p>smartctl (smartmontools)</p><p>Дополнительно в gnome доступен следующий инструмент: gnome-disk-utility</p><p>hdparm — Инструмент для тестирования скорости чтения (бенчмаркинг) и управления параметрами работы в основном HDD-дисков через низкоуровневые настройки.</p><p>badblocks — Утилита для прямого поиска сбойных секторов на поверхности диска.</p><p>fsck — стандартная утилита для проверки и исправления ошибок файловых систем (ext2/ext3/ext4, FAT, NTFS и др.)</p><p>testdisk</p><p>btrfs check</p>',12)]))}const h=a(i,[["render",o]]);export{m as __pageData,h as default};
